import React, { useState } from "react";
import { Controlled as CodeMirror } from "react-codemirror2";
import api from "../services/api";
import "codemirror/lib/codemirror.css";
import "codemirror/mode/python/python";

function CodeBlock({ block, onBlockChange }) {
  const [code, setCode] = useState(block.codeblock?.code || "");
  const [output, setOutput] = useState(block.codeblock?.output || "");
  const [aiPrompt, setAiPrompt] = useState("");

  const handleRunCode = async () => {
    try {
      // 1. Update block code first, so /run/ has the latest code
      await api.put(`/dashboard/blocks/${block.id}/`, {
        block_type: "code",
        codeblock: {
          language: "python",
          code,
        },
        position: block.position,
      });

      // 2. Then call /run/
      const response = await api.post(`/dashboard/blocks/${block.id}/run/`);
      setOutput(response.data.codeblock.output);
      onBlockChange(); // refresh parent if needed
    } catch (err) {
      console.error(err);
    }
  };

  const handleGenerateCode = async () => {
    if (!aiPrompt) {
      alert("Please enter a prompt for the AI");
      return;
    }
    try {
      // POST the user prompt to generate code
      const response = await api.post(`/dashboard/blocks/${block.id}/generate_code/`, {
        prompt: aiPrompt,
      });
      // The updated block with the new code is returned
      const newCode = response.data.codeblock.code;
      setCode(newCode);
      setAiPrompt("");
      alert("Code generated successfully!");
      onBlockChange();
    } catch (err) {
      console.error(err);
      alert("Error generating code");
    }
  };

  return (
    <div style={{ border: "1px solid #ccc", padding: "8px", margin: "8px 0" }}>
      <h4>Code Block</h4>
      <CodeMirror
        value={code}
        options={{
          mode: "python",
          theme: "default",
          lineNumbers: true,
        }}
        onBeforeChange={(editor, data, value) => {
          setCode(value);
        }}
      />
      <br />
      <button onClick={handleRunCode}>Run</button>
      <pre style={{ backgroundColor: "#f4f4f4", padding: "8px" }}>
        {output}
      </pre>

      <div style={{ marginTop: "1rem" }}>
        <h5>Generate code via AI</h5>
        <input
          type="text"
          placeholder="Enter your prompt"
          value={aiPrompt}
          onChange={(e) => setAiPrompt(e.target.value)}
        />
        <button onClick={handleGenerateCode}>Generate</button>
      </div>
    </div>
  );
}

export default CodeBlock;
