// src/pages/DashboardDetailPage.jsx
import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import api from "../services/api";
import Block from "../components/Block";

function DashboardDetailPage() {
  const { id } = useParams(); // dashboard_id
  const [dashboard, setDashboard] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    fetchDashboard();
  }, [id]);

  const fetchDashboard = async () => {
    try {
      const response = await api.get(`/dashboard/${id}/`);
      setDashboard(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleFileChange = (e) => {
    if (e.target.files.length > 0) {
      setSelectedFile(e.target.files[0]);
    }
  };

  const uploadCSV = async () => {
    if (!selectedFile) return;
    const formData = new FormData();
    formData.append("file", selectedFile);
    try {
      await api.post(`/dashboard/${id}/upload/`, formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("File uploaded successfully");
      setSelectedFile(null);
    } catch (err) {
      console.error(err);
      alert("File upload error");
    }
  };

  // Render all blocks
  // - We'll create a separate <Block /> component that decides
  //   whether to display a TextBlock or CodeBlock

  return (
    <div>
      <h2>Dashboard Detail: {dashboard?.title}</h2>
      <div>
        <h4>Upload CSV</h4>
        <input type="file" accept=".csv" onChange={handleFileChange} />
        <button onClick={uploadCSV}>Upload</button>
      </div>

      <div>
        <h3>Blocks</h3>
        {dashboard?.blocks?.map((block) => (
          <Block
            key={block.id}
            block={block}
            onBlockChange={fetchDashboard} // refresh after edits
          />
        ))}
      </div>
    </div>
  );
}

export default DashboardDetailPage;
